import React from 'react';
import styled, { keyframes } from 'styled-components';
import profileImage from '../assets/images/Screenshot_2024-06-11_at_9.40.08_PM-removebg-preview.png';

// Keyframes for typing effect
const typing = keyframes`
  from { width: 0 }
  to { width: 100% }
`;

const blink = keyframes`
  from, to { border-color: transparent }
  50% { border-color: orange; }
`;

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  padding: 20px;
`;

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  max-width: 1200px;
`;

const TextSection = styled.div`
  flex: 1;
  text-align: left;
`;

const Title = styled.h1`
  font-size: 4em;
  margin: 0;
  display: flex;
  flex-direction: column;
`;

const Subtitle = styled.h2`
  font-size: 2em;
  margin: 0;
  color: #00ff99;
`;

const Description = styled.p`
  font-size: 1.2em;
  color: #ccc;
`;

const Button = styled.a`
  display: inline-block;
  margin-top: 20px;
  padding: 10px 20px;
  font-size: 1.2em;
  color: white;
  background-color: #ffa500;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    background-color: #ff8c00;
  }
`;

const ImageSection = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProfileImage = styled.img`
  width: 400px;
  border-radius: 50%;
  border: 5px solid #00ff99;
  object-fit: cover;
`;

const Name = styled.span`
  display: inline-block;
  white-space: nowrap;
  overflow: hidden;
  border-right: 0.15em solid orange; /* Adjust as needed */
  animation:
    ${typing} 2s steps(12, end) forwards, /* Typing animation */
    ${blink} 0.75s step-end infinite; /* Cursor blink animation */
  font-size: 1.2em;
`;

const Home = () => {
  return (
    <Container>
      <Content>
        <TextSection>
          <Subtitle>Introducing</Subtitle>
          <Title>
            Hello, I'm <Name>Siddhesh Kulkarni</Name>
          </Title>
          <Description>
            Student and Developer
          </Description>
          <Button href="https://github.com/Sidmsu">GitHub</Button>
          <Button href="www.linkedin.com/in/siddhesh-kulkarni05">LinkedIn</Button>
          <Button href="#">Twitter</Button>
          <Button href="#">Instagram</Button>

        </TextSection>
        <ImageSection>
          <ProfileImage src={profileImage} alt="Profile" />
        </ImageSection>
      </Content>
    </Container>
  );
};

export default Home;
