import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee, faGlobe, faBriefcase, faEnvelope } from '@fortawesome/free-solid-svg-icons';
import { faFacebook, faInstagram, faGithub, faLinkedin, faTwitter } from '@fortawesome/free-brands-svg-icons';

const wave = keyframes`
  0% { transform: rotate(0.0deg); }
  10% { transform: rotate(14deg); }
  20% { transform: rotate(-8deg); }
  30% { transform: rotate(14deg); }
  40% { transform: rotate(-4deg); }
  50% { transform: rotate(10.0deg); }
  60% { transform: rotate(0.0deg); }
  100% { transform: rotate(0.0deg); }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  text-align: center;
  padding: 20px;
`;

const ProfileContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 40px;
  margin-top: 50px;
`;

const ProfilePic = styled.img`
  width: 400px;
  height: 400px;
  border-radius: 50%;
  border: 10px solid #00ff99;
  object-fit: cover;
`;

const Info = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const Name = styled.h1`
  font-size: 2.5em;
  margin: 0;
  display: flex;
  align-items: center;
`;

const Wave = styled.span`
  display: inline-block;
  animation: ${wave} 2s infinite;
  transform-origin: 70% 70%;
  font-size: 1.5em;
  margin-left: 10px;
`;

const JobTitle = styled.h2`
  margin: 0;
  color: #00ff99;
`;

const Details = styled.div`
  margin-top: 20px;
`;

const DetailItem = styled.div`
  display: flex;
  align-items: center;
  margin: 5px 0;
  font-size: 1.2em;

  svg {
    margin-right: 10px;
  }
`;

const SocialLinks = styled.div`
  margin-top: 20px;
  display: flex;
  gap: 20px;

  svg {
    cursor: pointer;
    font-size: 1.5em;
    &:hover {
      color: #00ff99;
    }
  }
`;

const Home = () => {
  return (
    <Container>
      <ProfileContainer>
        <ProfilePic src="src/assets/images/Screenshot_2024-06-11_at_9.40.08_PM-removebg-preview.png" alt="Profile" />
        <Info>
          <Name>
            Hi, I'm John<Wave>👋</Wave>
          </Name>
          <JobTitle>I'm a Full Stack Developer.</JobTitle>
          <Details>
            <DetailItem>
              <FontAwesomeIcon icon={faCoffee} /> fueled by coffee
            </DetailItem>
            <DetailItem>
              <FontAwesomeIcon icon={faGlobe} /> based in the US
            </DetailItem>
            <DetailItem>
              <FontAwesomeIcon icon={faBriefcase} /> Systems Engineer at Google
            </DetailItem>
            <DetailItem>
              <FontAwesomeIcon icon={faEnvelope} /> johnsmith@gmail.com
            </DetailItem>
          </Details>
          <SocialLinks>
            <FontAwesomeIcon icon={faFacebook} />
            <FontAwesomeIcon icon={faInstagram} />
            <FontAwesomeIcon icon={faGithub} />
            <FontAwesomeIcon icon={faLinkedin} />
            <FontAwesomeIcon icon={faTwitter} />
          </SocialLinks>
        </Info>
      </ProfileContainer>
    </Container>
  );
};

export default Home;
