import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoffee, faGlobe, faBriefcase, faEnvelope } from '@fortawesome/free-solid-svg-icons';
import { faFacebook, faInstagram, faGithub, faLinkedin, faTwitter } from '@fortawesome/free-brands-svg-icons';

const wave = keyframes`
  0% { transform: rotate(0.0deg); }
  10% { transform: rotate(14deg); }
  20% { transform: rotate(-8deg); }
  30% { transform: rotate(14deg); }
  40% { transform: rotate(-4deg); }
  50% { transform: rotate(10.0deg); }
  60% { transform: rotate(0.0deg); }
  100% { transform: rotate(0.0deg); }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  text-align: center;
`;

const ProfilePic = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  margin-bottom: 20px;
`;

const Info = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Name = styled.h1`
  font-size: 2em;
  margin: 0;
`;

const Wave = styled.span`
  display: inline-block;
  animation: ${wave} 2s infinite;
  transform-origin: 70% 70%;
  font-size: 2em;
`;

const JobTitle = styled.h2`
  margin: 0;
  color: #00ff99;
`;

const Details = styled.div`
  margin-top: 20px;
`;

const DetailItem = styled.div`
  display: flex;
  align-items: center;
  margin: 5px 0;

  svg {
    margin-right: 10px;
  }
`;

const SocialLinks = styled.div`
  margin-top: 20px;
  display: flex;
  gap: 20px;

  svg {
    cursor: pointer;
    font-size: 1.5em;
    &:hover {
      color: #00ff99;
    }
  }
`;

const Home = () => {
  return (
    <Container>
      <ProfilePic src="https://via.placeholder.com/150" alt="Profile" />
      <Info>
        <Name>
          Hi, I'm Siddhesh<Wave>ðŸ‘‹</Wave>
        </Name>
        <JobTitle>I'm a Full Stack Developer.</JobTitle>
        <Details>
          <DetailItem>
            <FontAwesomeIcon icon={faCoffee} /> fueled by coffee
          </DetailItem>
          <DetailItem>
            <FontAwesomeIcon icon={faGlobe} /> based in the US
          </DetailItem>
          <DetailItem>
            <FontAwesomeIcon icon={faBriefcase} /> Systems Engineer at Google
          </DetailItem>
          <DetailItem>
            <FontAwesomeIcon icon={faEnvelope} /> johnsmith@gmail.com
          </DetailItem>
        </Details>
        <SocialLinks>
          <FontAwesomeIcon icon={faFacebook} />
          <FontAwesomeIcon icon={faInstagram} />
          <FontAwesomeIcon icon={faGithub} />
          <FontAwesomeIcon icon={faLinkedin} />
          <FontAwesomeIcon icon={faTwitter} />
        </SocialLinks>
      </Info>
    </Container>
  );
};

export default Home;
