import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  text-align: center;
  padding: 20px;
`;

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  max-width: 1200px;
`;

const TextSection = styled.div`
  flex: 1;
`;

const Title = styled.h1`
  font-size: 4em;
  margin: 0;
`;

const Subtitle = styled.h2`
  font-size: 2em;
  margin: 0;
  color: #00ff99;
`;

const Description = styled.p`
  font-size: 1.2em;
  color: #ccc;
`;

const Button = styled.a`
  display: inline-block;
  margin-top: 20px;
  padding: 10px 20px;
  font-size: 1.2em;
  color: white;
  background-color: #ffa500;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    background-color: #ff8c00;
  }
`;

const ImageSection = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProfileImage = styled(motion.img)`
  width: 300px;
  border-radius: 50%;
  border: 5px solid #00ff99;
  object-fit: cover;
`;

const Home = () => {
  return (
    <Container>
      <Content>
        <TextSection>
          <Subtitle>Introducing</Subtitle>
          <Title>Hello, I'm Jessy Walter</Title>
          <Description>
            Since beginning my journey as a freelance designer nearly 7 years ago, I've done remote work for agencies, consulted for startups, and collaborated with talented people to create digital products.
          </Description>
          <Button href="#">Contact Me</Button>
        </TextSection>
        <ImageSection>
          <ProfileImage
            src="https://via.placeholder.com/300"
            alt="Profile"
            initial={{ y: 200, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 1 }}
          />
        </ImageSection>
      </Content>
    </Container>
  );
};

export default Home;
