import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub } from '@fortawesome/free-brands-svg-icons';

const PortfolioContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #1e1e1e;
  color: white;
  min-height: 100vh;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
  width: 100%;
  max-width: 1200px;
  margin-top: 20px;
`;

const Card = styled(motion.div)`
  background-color: #2d2d2d;
  border-radius: 5px;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s;

  &:hover {
    transform: scale(1.05);
  }
`;

const CardImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const CardContent = styled.div`
  padding: 20px;
`;

const CardTitle = styled.h3`
  margin: 0;
  color: #00ff99;
`;

const CardDescription = styled.p`
  margin-top: 10px;
  color: #ccc;
`;

const CardLink = styled.a`
  display: inline-block;
  margin-top: 10px;
  color: #00ff99;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const Portfolio = () => {
  return (
    <PortfolioContainer>
      <h1>My Portfolio</h1>
      <Grid>
        {Array.from({ length: 8 }).map((_, index) => (
          <Card
            key={index}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
          >
            <CardImage src="https://via.placeholder.com/300" alt={`Project ${index + 1}`} />
            <CardContent>
              <CardTitle>Project {index + 1}</CardTitle>
              <CardDescription>
                This is a brief description of the project. It showcases the main features and technologies 
              </CardDescription>
              <CardLink href={`https://github.com/username/project${index + 1}`} target="_blank" rel="noopener noreferrer">
                <FontAwesomeIcon icon={faGithub} /> View Project
              </CardLink>
            </CardContent>
          </Card>
        ))}
      </Grid>
    </PortfolioContainer>
  );
};

export default Portfolio;
