import React, { useRef } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import Home from './components/Home';
import About from './components/About';
import Portfolio from './components/Portfolio';
import Contact from './components/Contact';
import Navbar from './components/Navbar';
import styled from 'styled-components';

const AppContainer = styled.div`
  font-family: 'Arial, sans-serif';
`;

const ScrollToSection = ({ children }) => {
  const aboutRef = useRef(null);
  const contactRef = useRef(null);
  const history = useHistory();

  const scrollToSection = (ref) => {
    if (ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleScrollToAbout = () => {
    history.push('/');
    setTimeout(() => scrollToSection(aboutRef), 100); // Delay to ensure the page loads first
  };

  const handleScrollToContact = () => {
    history.push('/');
    setTimeout(() => scrollToSection(contactRef), 100); // Delay to ensure the page loads first
  };

  return React.cloneElement(children, {
    scrollToAbout: handleScrollToAbout,
    scrollToContact: handleScrollToContact,
    aboutRef,
    contactRef,
  });
};

const App = () => {
  return (
    <Router>
      <AppContainer>
        <ScrollToSection>
          <Navbar />
        </ScrollToSection>
        <Switch>
          <Route
            exact
            path="/"
            render={({ aboutRef, contactRef }) => (
              <>
                <Home />
                <div ref={aboutRef}>
                  <About />
                </div>
                <div ref={contactRef}>
                  <Contact />
                </div>
              </>
            )}
          />
          <Route path="/portfolio" component={Portfolio} />
        </Switch>
      </AppContainer>
    </Router>
  );
};

export default App;
