import React, { useRef } from 'react';
import Home from './components/Home';
import About from './components/About';
import Projects from './components/Projects';  // Import Projects component
import Contact from './components/Contact';    // Import Contact component
import Navbar from './components/Navbar';
import styled from 'styled-components';

const AppContainer = styled.div`
  font-family: 'Arial, sans-serif';
`;

const App = () => {
  const aboutRef = useRef(null);
  const projectsRef = useRef(null);  // Create ref for Projects section
  const contactRef = useRef(null);   // Create ref for Contact section

  const scrollToSection = (ref) => {
    if (ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <AppContainer>
      <Navbar
        scrollToAbout={() => scrollToSection(aboutRef)}
        scrollToProjects={() => scrollToSection(projectsRef)}  // Pass function for Projects section
        scrollToContact={() => scrollToSection(contactRef)}    // Pass function for Contact section
      />
      <Home />
      <div ref={aboutRef}>
        <About />
      </div>
      <div ref={projectsRef}>
        <Projects />
      </div>
      <div ref={contactRef}>
        <Contact />
      </div>
    </AppContainer>
  );
};

export default App;
