import React from 'react';
import styled, { keyframes } from 'styled-components';
import profileImage from '../assets/images/Screenshot_2024-06-11_at_9.40.08_PM-removebg-preview.png';

// Keyframes for typing effect
const typing = keyframes`
  from { width: 0 }
  to { width: 100% }
`;

const blink = keyframes`
  from, to { border-color: transparent }
  50% { border-color: orange; }
`;

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  padding: 20px;
`;

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  max-width: 1200px;
`;

const TextSection = styled.div`
  flex: 1;
  text-align: left;
`;

const Title = styled.h1`
  font-size: 4em;
  margin: 0;
  white-space: nowrap;
  overflow: hidden;
  border-right: 0.15em solid orange; /* Adjust as needed */
  width: 10ch; /* Adjust to match the length of the text */
  animation:
    ${typing} 4s steps(12, end),
    ${blink} 0.75s step-end infinite;
`;

const Subtitle = styled.h2`
  font-size: 2em;
  margin: 0;
  color: #00ff99;
`;

const Description = styled.p`
  font-size: 1.2em;
  color: #ccc;
`;

const Button = styled.a`
  display: inline-block;
  margin-top: 20px;
  padding: 10px 20px;
  font-size: 1.2em;
  color: white;
  background-color: #ffa500;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    background-color: #ff8c00;
  }
`;

const ImageSection = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProfileImage = styled.img`
  width: 400px;
  border-radius: 50%;
  border: 5px solid #00ff99;
  object-fit: cover;
`;

const Home = () => {
  return (
    <Container>
      <Content>
        <TextSection>
          <Subtitle>Introducing</Subtitle>
          <Title>Hello, I'm </Title>
          <h2>Jessy Walter</h2>
          <Description>
            Since beginning my journey as a freelance designer nearly 7 years ago, I've done remote work for agencies, consulted for startups, and collaborated with talented people to create digital products.
          </Description>
          <Button href="#">Contact Me</Button>
        </TextSection>
        <ImageSection>
          <ProfileImage src={profileImage} alt="Profile" />
        </ImageSection>
      </Content>
    </Container>
  );
};

export default Home;
