import React from 'react';
import styled, { keyframes } from 'styled-components';
import profileImage from '../assets/images/Screenshot_2024-06-11_at_9.40.08_PM-removebg-preview.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faLinkedin, faTwitter, faInstagram } from '@fortawesome/free-brands-svg-icons';

// Keyframes for typing effect
const typing = keyframes`
  from { width: 0 }
  to { width: 100% }
`;

const blink = keyframes`
  from, to { border-color: transparent }
  50% { border-color: orange; }
`;

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  padding: 20px;
`;

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  max-width: 1200px;
`;

const TextSection = styled.div`
  flex: 1;
  text-align: left;
`;

const Title = styled.h1`
    font-family: 'Public Sans', sans-serif;
  font-size: 4em;
  margin: 0;
  display: flex;
  flex-direction: column;
`;



const Description = styled.p`
    font-family: 'Courier New', Courier, monospace;
  font-size: 1.8em;
  color: #ccc;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.a`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 50px;
  height: 50px;
  font-size: 1.5em;
  color: white;
  background-color: #00ff99;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  text-decoration: none;
  transition: background-color 0.5s;

  &:hover {
    background-color: #00cc7a;
  }
`;

const ImageSection = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ProfileImage = styled.img`
  width: 400px;
  border-radius: 50%;
  border: 5px solid #00ff99;
  object-fit: cover;
`;

const Name = styled.span`
  font-family: 'Public Sans', sans-serif;
  display: inline-block;
  white-space: nowrap;
  overflow: hidden;
  border-right: 0.15em solid orange; /* Adjust as needed */
  animation:
    ${typing} 2s steps(12, end) forwards, /* Typing animation */
    ${blink} 0.75s step-end infinite; /* Cursor blink animation */
  font-size: 1.2em;
`;

const Home = () => {
  return (
    <Container>
      <Content>
        <TextSection>
          <Title>
            Hello, I'm <Name>Siddhesh Kulkarni</Name>
          </Title>
          <Description>Student and Developer</Description>
          <ButtonContainer>
            <Button href="https://github.com/Sidmsu" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faGithub} />
            </Button>
            <Button href="https://linkedin.com/in/siddhesh-kulkarni05" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faLinkedin} />
            </Button>
            <Button href="https://x.com/sidmsu27" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faTwitter} />
            </Button>
            <Button href="https://www.instagram.com/_siddhesh.kulkarni_/" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faInstagram} />
            </Button>
          </ButtonContainer>
        </TextSection>
        <ImageSection>
          <ProfileImage src={profileImage} alt="Profile" />
        </ImageSection>
      </Content>
    </Container>
  );
};

export default Home;
